mapper.insert:

		 SELECT
		        NVL(TO_NUMBER(MAX(CRD_ORDER)),0)+1
	       FROM CARD
          WHERE BRD_NO = #{brdNo}

		INSERT
	  INTO CARD A
	  (
	     A.CRD_NO
       , A.CRD_NAME
       , A.BRD_NO
       , A.CRD_ORDER
       , A.CRD_TYPE
       , A.MEM_NO
       , A.CRD_TXT
	    )
	  VALUES
	  (
	    SEQ_CRD_NO.NEXTVAL
	  , #{title}
	  , #{brdNo}
	  , #{order}
	  , #{type}
	  , #{memNo}
	  , #{txt}
	  )

mapper.update:
UPDATE CARD A
   SET A.CRD_NAME = #{title}
     , A.CRD_ORDER = #{order}
     , A.CRD_TYPE = #{type}
     , A.CRD_TXT = #{txt}
 WHERE A.CRD_NO = #{no}

UPDATE CRD_SCHEDULE A
   SET A.SCH_ALERT = #{alert}
     , A.SCH_PLACE = #{place}
     , A.SCH_START_DATE = TO_DATE(#{scheduleStartDate}, 'YYYYMMDDHH24MISS')
     , A.SCH_END_DATE = TO_DATE(#{scheduleEndDate}, 'YYYYMMDDHH24MISS')
 WHERE A.CRD_NO = #{no}

UPDATE CRD_SCH_ATTEND_LIST A
   SET A.MEM_NO = #{memNo}
 WHERE A.CRD_NO = #{crdNo}

UPDATE CARD A
   SET A.CRD_DEL_YN = 'Y'
 WHERE A.CRD_NO = #{no}
