mapper.insert:
        INSERT 
          INTO TBL_MEMBER A
        (
          A.MEMBER_NO, A.EMAIL, A.MEMBER_PWD
        , A.MEMBER_NAME, A.PHONE
        ) 
		VALUES
		(
		  SEQ_MEMBER_NO.NEXTVAL, #{email}, #{password}
		, #{name}, #{phone}
		)

mapper.update:
UPDATE TBL_MEMBER A 
   SET A.MEMBER_NAME = #{name}
     , A.PHONE = #{phone}
 WHERE A.EMAIL = #{email}
