mapper.insert:
        INSERT 
          INTO MEMBER A
        (
          A.MEM_NO, A.MEM_ID, A.MEM_PWD
        , A.MEM_NAME, A.PHONE
        ) 
		VALUES
		(
		  SEQ_MEM_NO.NEXTVAL, #{email}, #{password}
		, #{name}, #{phone}
		)

mapper.update:
UPDATE MEMBER A 
   SET A.MEM_NAME = #{name}
     , A.PHONE = #{phone}
 WHERE A.MEM_ID = #{email}

UPDATE MEMBER A 
   SET A.MEM_PWD = #{password}
 WHERE A.MEM_ID = #{email}
