mapper.select:
		SELECT PJT_NO
          FROM BACKLOG
         WHERE BLG_NO = #{blgNo}

mapper.insert:
INSERT
  INTO REVIEW
( 
  REVIEW_NO
, REVIEW_LIKE_TXT
, REVIEW_LEARN_TXT
, REVIEW_MISS_TXT
, SPR_NO
, MEM_NO
)
VALUES
(
  SEQ_REVIEW_NO.NEXTVAL
, #{reviewLikeTxt}
, #{reviewLearnTxt}
, #{reviewMissTxt}
, #{sprNo}
, #{memNo}
)

mapper.update:
UPDATE
       REVIEW
   SET REVIEW_LIKE_TXT = #{reviewLikeTxt}
     , REVIEW_LEARN_TXT = #{reviewLearnTxt}
     , REVIEW_MISS_TXT = #{reviewMissTxt}
 WHERE MEM_NO = #{memNo}

mapper.delete:
DELETE
  FROM REVIEW
 WHERE MEM_NO = #{memNo}
   AND SPR_NO = #{sprNo}
